mapper.update:
UPDATE BLOG
   SET HIT = HIT + 1
 WHERE BLOG_NO = #{blogNo}

mapper.select:
SELECT BLOG_NO, TITLE, CONTENT, HIT, IP, CREATE_DATE, MODIFY_DATE
  FROM BLOG
 WHERE BLOG_NO = #{blogNo}

mapper.update:
UPDATE BLOG
   SET TITLE = #{title}
     , CONTENT = #{content}
     , MODIFY_DATE = SYSDATE
 WHERE BLOG_NO = #{blogNo}
